const startAdventure=()=>{const e=prompt('You are in a dark forest. Do you go left or right? Type "left" or "right".');if("left"===e){const e=prompt('You find a magical pond. Do you drink from it or leave it alone? Type "drink" or "leave".');"drink"===e?alert("The pond grants you eternal wisdom. You win!"):"leave"===e?alert("A mysterious creature appears and leads you out of the forest. You safely return home."):alert("Invalid choice. You wander the forest forever.")}else if("right"===e){const e=prompt('You come across a dragon. Do you fight it or try to befriend it? Type "fight" or "befriend".');"fight"===e?alert("The dragon is too powerful. You are defeated."):"befriend"===e?alert("The dragon becomes your ally and helps you find treasure. You win!"):alert("Invalid choice. The dragon finds you and you are defeated.")}else alert("Invalid choice. You are lost in the forest.")};startAdventure();
